module Validation
  ERRORS = { not_exist: 'The variable does not exist',
             no_match_class: 'The type of data is invalid',
             incorr_format: 'The format of data is invalid' }

  def self.included(base)
    base.extend ClassMethods
    base.send :include, InstanceMethods
  end

  module ClassMethods
    def validate(name, type, *args)
      var_name = "@#{name}".to_sym
      errors = "@errors".to_sym
      define_method("errors") { instance_variable_get(errors) }
      define_method("validate_#{type}") do
        var = instance_variable_get(var_name)
        err = instance_variable_get(errors) || []
        case type
        when :presence
          error = ERRORS[:not_exist] if var.nil? || var.empty?
        when :format
          error = ERRORS[:incorr_format] if var !~ args[0]
        when :type
          error = ERRORS[:no_match_class] unless var.instance_of?(args[0])
        end
        err << error
        instance_variable_set(errors, err)
      end
    end
  end

  module InstanceMethods
    def validate!
      methods_arr = self.methods.grep /^validate_/
      methods_arr.each { |m| self.send(m) }
      errors.compact!
      raise self.errors.join('; ') unless self.errors.empty?                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
    end

    def valid?
      validate!
      true
    rescue StandardError
      false
    end
  end
end
